import { useState } from 'react';
import { useAuth } from '../../context/AuthContext';

export default function TableActions({ table, onUpdate }) {
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);

  const handleAction = async () => {
    setLoading(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 500));

      const newStatus = table.status === 'available' ? 'occupied' : 'available';
      onUpdate({
        ...table,
        status: newStatus,
        current_session_minutes: newStatus === 'occupied' ? 0 : undefined
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="mt-3 space-x-2">
      <button
        onClick={handleAction}
        disabled={loading}
        className={`${
          table.status === 'available' 
            ? 'bg-blue-500 hover:bg-blue-600' 
            : 'bg-red-500 hover:bg-red-600'
        } text-white px-3 py-1 rounded text-sm transition-colors`}
      >
        {loading ? 'Processing...' : table.status === 'available' ? 'Start Session' : 'End Session'}
      </button>
    </div>
  );
}
