import { useState } from 'react';
import TableActions from './TableActions';

const mockTables = [
  { id: 1, table_number: 'B1', table_type: 'billiard', status: 'occupied', current_session_minutes: 45 },
  { id: 2, table_number: 'B2', table_type: 'billiard', status: 'available' },
  { id: 3, table_number: 'B3', table_type: 'billiard', status: 'maintenance' },
  { id: 4, table_number: 'R1', table_type: 'restaurant', status: 'occupied', current_session_minutes: 30 },
  { id: 5, table_number: 'R2', table_type: 'restaurant', status: 'available' },
];

export default function TableStatus() {
  const [tables, setTables] = useState(mockTables);

  const handleTableUpdate = (updatedTable) => {
    setTables(tables.map(t => 
      t.id === updatedTable.id ? updatedTable : t
    ));
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-xl font-semibold mb-4">Table Status</h2>
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-5 gap-3">
        {tables.map(table => (
          <div 
            key={table.id}
            className={`p-3 rounded-lg border-2 ${
              table.status === 'occupied' ? 'bg-red-50 border-red-200' :
              table.status === 'available' ? 'bg-green-50 border-green-200' :
              'bg-yellow-50 border-yellow-200'
            }`}
          >
            <div className="font-bold">{table.table_number}</div>
            <div className="text-sm text-gray-600 capitalize">{table.table_type}</div>
            <div className="text-sm mt-1">
              Status: <span className="capitalize font-medium">{table.status}</span>
            </div>
            {table.status === 'occupied' && (
              <div className="text-xs mt-1">
                {Math.floor(table.current_session_minutes/60)}h {table.current_session_minutes%60}m
              </div>
            )}
            <TableActions table={table} onUpdate={handleTableUpdate} />
          </div>
        ))}
      </div>
    </div>
  );
}
