import { useState, useEffect } from 'react';
import api from '../../services/api';

export default function CreateOrder({ tableId }) {
  const [items, setItems] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [selectedItem, setSelectedItem] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchInventory = async () => {
      const { data } = await api.get('/inventory');
      setInventory(data.filter(item => item.is_active));
    };
    fetchInventory();
  }, []);

  const handleAddItem = () => {
    const item = inventory.find(i => i.id === parseInt(selectedItem));
    if (!item) return;

    setItems([...items, {
      id: item.id,
      name: item.name,
      price: item.unit_price,
      quantity
    }]);
    setQuantity(1);
  };

  const handleSubmit = async () => {
    setLoading(true);
    try {
      await api.post('/orders', {
        table_id: tableId,
        items: items.map(item => ({
          item_id: item.id,
          quantity: item.quantity
        }))
      });
      setItems([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow">
      <h3 className="font-semibold mb-3">Create Order</h3>
      <div className="flex gap-2 mb-4">
        <select
          className="flex-1 border p-2 rounded"
          value={selectedItem}
          onChange={(e) => setSelectedItem(e.target.value)}
        >
          <option value="">Select item</option>
          {inventory.map(item => (
            <option key={item.id} value={item.id}>
              {item.name} (₱{item.unit_price})
            </option>
          ))}
        </select>
        <input
          type="number"
          min="1"
          value={quantity}
          onChange={(e) => setQuantity(parseInt(e.target.value))}
          className="w-16 border p-2 rounded"
        />
        <button
          onClick={handleAddItem}
          className="bg-green-500 text-white px-3 rounded"
          disabled={!selectedItem}
        >
          Add
        </button>
      </div>

      {items.length > 0 && (
        <>
          <div className="border-t pt-3 mb-3">
            {items.map((item, index) => (
              <div key={index} className="flex justify-between items-center py-2 border-b">
                <div>
                  <span>{item.name}</span>
                  <span className="text-gray-500 text-sm ml-2">x{item.quantity}</span>
                </div>
                <div>
                  <span>₱{(item.price * item.quantity).toFixed(2)}</span>
                  <button 
                    onClick={() => setItems(items.filter((_, i) => i !== index))}
                    className="ml-2 text-red-500"
                  >
                    ×
                  </button>
                </div>
              </div>
            ))}
          </div>
          <div className="flex justify-between font-bold mb-3">
            <span>Total:</span>
            <span>₱{items.reduce((sum, item) => sum + (item.price * item.quantity), 0).toFixed(2)}</span>
          </div>
          <button
            onClick={handleSubmit}
            disabled={loading}
            className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
          >
            {loading ? 'Submitting...' : 'Submit Order'}
          </button>
        </>
      )}
    </div>
  );
}
